INSERT INTO steps (mainTableId,order,name) VALUES ("r312653",0,"Precaliente el horno a 200ºC con calor arriba y abajo. Moje y escurra una lámina de papel de hornear de aprox. 40x40 cm y forre un molde desmontable de Ø 24-26 cm (al ser una preparación líquida es necesario que el papel la recoja para que no se salga).");
