INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",0,"Precaliente el horno a 180°C. Engrase con la mantequilla los laterales de un molde desmontable de Ø 22 cm y cubra la base y los laterales con papel de hornear de manera que el papel sobresalga por 2-3 cm por encima del borde del molde.");
