INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",5,"Hornee durante 60 minutos (180°C). Deje enfriar el bizcocho dentro del molde durante 20 minutos y después desmolde sobre una rejilla. Lave y seque el vaso.");
INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",6,"Ponga en el vaso el chocolate blanco y ralle 8 seg/vel 8.");
INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",7,"Con la espátula, baje el chocolate hacia el fondo del vaso. Añada la mantequilla y funda 1 min/50°C/vel 3.");
INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",8,"Incorpore el queso cremoso, el zumo de naranja y el azúcar glas y mezcle 30 seg/vel 3.");
INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",9,"Con la espátula, baje los ingredientes hacia el fondo del vaso y vuelva a mezclar 30 seg/vel 4.");
INSERT INTO steps (mainTableId,order,name) VALUES ("r96912",10,"Corte el bizcocho por la mitad y rellene con la mitad del frosting. Cubra con la otra parte del bizcocho y extienda el resto del frosting sobre la superficie. Espolvoree con las nueces y deje enfriar 15 minutos en el frigorífico antes de servir.");
